mapper.select:
SELECT BOARD_NUM_NOTICE , TITLE_NOTICE , CONTENT_NOTICE , WRITER_NOTICE , CREATE_DATE_NOTICE , READ_CNT_NOTICE , BOARD_PW_NOTICE , ROW_NUM FROM ( SELECT BOARD_NUM_NOTICE , TITLE_NOTICE , CONTENT_NOTICE , WRITER_NOTICE , CREATE_DATE_NOTICE , READ_CNT_NOTICE , BOARD_PW_NOTICE , ROWNUM AS ROW_NUM FROM ( SELECT BOARD_NUM_NOTICE , TITLE_NOTICE , CONTENT_NOTICE , WRITER_NOTICE , TO_CHAR(CREATE_DATE_NOTICE, 'YYYY-MM-DD') AS CREATE_DATE_NOTICE , READ_CNT_NOTICE , BOARD_PW_NOTICE FROM BOARD_NOTICE
WHERE ${searchKeyword} LIKE '%'||LOWER(#{searchValue})||'%'
ORDER BY BOARD_NUM_NOTICE DESC ) ) WHERE ROW_NUM >= #{startNum} AND ROW_NUM <= #{endNum}

mapper.insert:
INSERT INTO BOARD_NOTICE (
BOARD_NUM_NOTICE
, TITLE_NOTICE
, WRITER_NOTICE
, CONTENT_NOTICE

	, FILE_NAME_NOTICE


	, BOARD_PW_NOTICE

) VALUES (
(SELECT NVL(MAX(BOARD_NUM_NOTICE), 0) + 1 FROM BOARD_NOTICE)
, #{titleNotice}
, #{writerNotice}
, #{contentNotice}

	, #{fileNameNotice}


	, #{boardPwNotice}

)

mapper.update:
UPDATE BOARD_NOTICE
SET
TITLE_NOTICE = #{titleNotice}
, CONTENT_NOTICE = #{contentNotice}

	, FILE_NAME_NOTICE = #{fileNameNotice}

WHERE BOARD_NUM_NOTICE = #{boardNumNotice}

mapper.insert:
INSERT INTO NOTICE_IMG (
NOTICE_IMG_CODE
, NOTICE_IMG_ORIGN_NAME
, NOTICE_IMG_ATTACHED_NAME
, BOARD_NUM_NOTICE
)

	SELECT #{imgInfo.noticeImgCode}
	, #{imgInfo.noticeImgOrignName}
	, #{imgInfo.noticeImgAttachedName}
	, #{imgInfo.boardNumNotice}
	FROM DUAL

mapper.select:
SELECT BOARD_NUM_FREE , TITLE_FREE , CONTENT_FREE , WRITER_FREE , CREATE_DATE_FREE , READ_CNT_FREE , BOARD_PW_FREE , ROW_NUM , TEAM_CODE FROM ( SELECT BOARD_NUM_FREE , TITLE_FREE , CONTENT_FREE , WRITER_FREE , CREATE_DATE_FREE , READ_CNT_FREE , BOARD_PW_FREE , ROWNUM AS ROW_NUM , TEAM_CODE FROM ( SELECT BOARD_NUM_FREE , TITLE_FREE , CONTENT_FREE , B.WRITER_FREE , TO_CHAR(CREATE_DATE_FREE, 'YYYY-MM-DD') AS CREATE_DATE_FREE , READ_CNT_FREE , BOARD_PW_FREE , M.TEAM_CODE FROM BOARD_FREE B, MEMBER M WHERE B.WRITER_FREE = M.MEMBER_ID
AND ${searchKeyword} LIKE '%'||LOWER(#{searchValue})||'%'
ORDER BY BOARD_NUM_FREE DESC ) ) WHERE ROW_NUM >= #{startNum} AND ROW_NUM <= #{endNum}

mapper.insert:
INSERT INTO BOARD_FREE (
BOARD_NUM_FREE
, TITLE_FREE
, WRITER_FREE
, CONTENT_FREE

	, FILE_NAME_FREE


	, BOARD_PW_FREE

) VALUES (
(SELECT NVL(MAX(BOARD_NUM_FREE), 0) + 1 FROM BOARD_FREE)
, #{titleFree}
, #{writerFree}
, #{contentFree}

	, #{fileNameFree}


	, #{boardPwFree}

)

mapper.update:
UPDATE BOARD_FREE
SET
TITLE_FREE = #{titleFree}
, CONTENT_FREE = #{contentFree}

	, FILE_NAME_FREE = #{fileNameFree}

WHERE BOARD_NUM_FREE = #{boardNumFree}

mapper.insert:
INSERT INTO FREE_IMG (
FREE_IMG_CODE
, FREE_IMG_ORIGN_NAME
, FREE_IMG_ATTACHED_NAME
, BOARD_NUM_FREE
)

	SELECT #{imgInfo.freeImgCode}
	, #{imgInfo.freeImgOrignName}
	, #{imgInfo.freeImgAttachedName}
	, #{imgInfo.boardNumFree}
	FROM DUAL
