mapper.insert:
INSERT INTO MATCH (
	MATCH_CODE
	, MATCH_WRITER
	, MATCH_INTRO	
	, MATCH_DATE
	, MATCH_START_TIME
	, MATCH_END_TIME
	, MATCH_POSSIBLE
	, MATCH_LOCATION
) VALUES (
	(SELECT 'MATCH_CODE_' || LPAD(NVL(MAX(TO_NUMBER(SUBSTR(MATCH_CODE, 12, 4))), 0)+1, 4, '0') FROM MATCH)
	, #{matchWriter}
	, #{matchIntro}
	, #{matchDate}
	, #{matchStartTime}
	, #{matchEndTime}
	, #{matchPossible}
	, #{matchLocation}
)

INSERT INTO MATCH_MANAGE (
	MATCH_MANAGE_CODE
	, MATCH_CODE
	, TEAM_CODE_AWAY
)
VALUES (
	(SELECT 'MATCH_MANAGE_CODE_' || LPAD(NVL(MAX(TO_NUMBER(SUBSTR(MATCH_MANAGE_CODE, 19, 4))), 0)+1, 4, '0') FROM MATCH_MANAGE)
	
	, #{matchCode}
	
	, #{teamCodeAway}
)
